{
  "version": "1.0.0",
  "name": "GitHub MCP Server",
  "description": "GitHub 模型上下文协议服务器配置",
  "servers": [
    {
      "id": "github-main",
      "name": "GitHub Main Server",
      "url": "https://api.github.com/mcp",
      "api_key": "${GITHUB_TOKEN}",
      "models": [
        "gpt-4o",
        "claude-3-sonnet-20240229",
        "gemini-pro"
      ],
      "features": [
        "context-sharing",
        "cross-platform",
        "real-time-sync"
      ]
    }
  ],
  "default_server": "github-main",
  "settings": {
    "max_context_length": 32768,
    "sync_interval": 5000,
    "enable_logging": true
  }
}
